mapper.insert:
insert
  into likey
  (
  	project_no,
  	member_no
  )
  values
  (
  	#{projectNo}
  , #{memberNo}
  )

mapper.select:
select count(*)
  from likey
 where project_no = #{projectNo}
   and member_no = #{memberNo}

mapper.delete:
delete from likey
 where project_no = #{projectNo}
   and member_no = #{memberNo}
